var _ = require('underscore');

function TestSuite() {
    if ( typeof(this._name) === 'undefined' ) {
        this._name = '<no name>';
    }
}

TestSuite.prototype.name = function(p_name) {
    if ( typeof(p_name) !== 'undefined' ) {
        this._name = p_name;
        return this;
    }
    return this._name;
};

TestSuite.prototype.runTests = function(testNames) {
    console.log('>>>> Entering Test Suite', this._name, '<<<<');
    for ( var f in this ) {
        if ( typeof(this[f]) === 'function' ) {
            console.log('xxxxxxxxxxxxxxxx', testNames);
            if ( f.substr(0, 'test'.length) === 'test' && (typeof(testNames) === 'undefined' || _(testNames).contains(f)) ) {
                console.log('>>>>> Running ', f, '<<<<<');
                this[f].call(this);
                console.log('<<<<< Finished running', f, '>>>>>');
            } else if ( f.substr(0, 'INACTIVE'.length) === 'INACTIVE' ) {
                console.warn('>>>>> Found inactive test:', f, '<<<<<');
            }
        } 
    }
    console.log('<<<< Leaving Test Suite', this._name, '>>>>');
};

exports.TestSuite = TestSuite;
